// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

/******************************************************************************\
* Author: Nick Mudge <nick@perfectabstractions.com> (https://twitter.com/mudgen)
* EIP-2535 Diamonds: https://eips.ethereum.org/EIPS/eip-2535
/******************************************************************************/

import { IDiamondCut } from "../interfaces/IDiamondCut.sol";
import { LibDiamond } from "../libraries/LibDiamond.sol";

// Remember to add the loupe functions from DiamondLoupeFacet to the diamond.
// The loupe functions are required by the EIP2535 Diamonds standard

contract DiamondCutFacet is IDiamondCut {
    /// @notice Add/replace/remove any number of functions and optionally execute
    ///         a function with delegatecall
    /// @param _diamondCut Contains the facet addresses and function selectors
    /// @param _init The address of the contract or facet to execute _calldata
    /// @param _calldata A function call, including function selector and arguments
    ///                  _calldata is executed with delegatecall on _init
    function diamondCut(
        FacetCut[] calldata _diamondCut,
        address _init,
        bytes calldata _calldata
    ) external override {
        LibDiamond.enforceIsContractOwner();
        LibDiamond.diamondCut(_diamondCut, _init, _calldata);
    }
}

contracts/facets/DiamondLoupeFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
/******************************************************************************\
* Author: Nick Mudge <nick@perfectabstractions.com> (https://twitter.com/mudgen)
* EIP-2535 Diamonds: https://eips.ethereum.org/EIPS/eip-2535
/******************************************************************************/

import { LibDiamond } from  "../libraries/LibDiamond.sol";
import { IDiamondLoupe } from "../interfaces/IDiamondLoupe.sol";
import { IERC165 } from "../interfaces/IERC165.sol";

// The functions in DiamondLoupeFacet MUST be added to a diamond.
// The EIP-2535 Diamond standard requires these functions.

contract DiamondLoupeFacet is IDiamondLoupe, IERC165 {
    // Diamond Loupe Functions
    ////////////////////////////////////////////////////////////////////
    /// These functions are expected to be called frequently by tools.
    //
    // struct Facet {
    //     address facetAddress;
    //     bytes4[] functionSelectors;
    // }

    /// @notice Gets all facets and their selectors.
    /// @return facets_ Facet
    function facets() external override view returns (Facet[] memory facets_) {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        uint256 numFacets = ds.facetAddresses.length;
        facets_ = new Facet[](numFacets);
        for (uint256 i; i < numFacets; i++) {
            address facetAddress_ = ds.facetAddresses[i];
            facets_[i].facetAddress = facetAddress_;
            facets_[i].functionSelectors = ds.facetFunctionSelectors[facetAddress_].functionSelectors;
        }
    }

    /// @notice Gets all the function selectors provided by a facet.
    /// @param _facet The facet address.
    /// @return facetFunctionSelectors_
    function facetFunctionSelectors(address _facet) external override view returns (bytes4[] memory facetFunctionSelectors_) {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        facetFunctionSelectors_ = ds.facetFunctionSelectors[_facet].functionSelectors;
    }

    /// @notice Get all the facet addresses used by a diamond.
    /// @return facetAddresses_
    function facetAddresses() external override view returns (address[] memory facetAddresses_) {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        facetAddresses_ = ds.facetAddresses;
    }

    /// @notice Gets the facet that supports the given selector.
    /// @dev If facet is not found return address(0).
    /// @param _functionSelector The function selector.
    /// @return facetAddress_ The facet address.
    function facetAddress(bytes4 _functionSelector) external override view returns (address facetAddress_) {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        facetAddress_ = ds.selectorToFacetAndPosition[_functionSelector].facetAddress;
    }

    // This implements ERC-165.
    function supportsInterface(bytes4 _interfaceId) external override view returns (bool) {
        LibDiamond.DiamondStorage storage ds = LibDiamond.diamondStorage();
        return ds.supportedInterfaces[_interfaceId];
    }
}

contracts/facets/GameStorageFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;

// import "@openzeppelin/contracts/utils/Context.sol";

import { LibDiamond } from "../libraries/LibDiamond.sol";

import { WithStorage } from "../libraries/LibStorage.sol"; 

contract GameStorageFacet is WithStorage {
  modifier onlyAdminOrCore() {
    require(msg.sender == gs().diamondAddress || msg.sender == LibDiamond.contractOwner(), "Only the Core or Admin addresses can perform this action");
    _;
  }
  modifier testPatchExists(uint256 patchVersion) {
    require(gs().testPatchCount >= patchVersion, 'Test Patch does not exist');
    _;

  }
  function isPaused() external view returns (bool) {
    return gs().paused;
  }
  function getMowseTokenIdCounter() external view returns (uint256) {
    return gs().mowseTokenIdCounter;
  }
  // TestPatch methods
  function getPatchVersion() external view returns (uint256) {
    return gs().testPatchCount;
  }
  function getUniqueInteractionCount(uint256 patchVersion) external view testPatchExists(patchVersion) returns(uint256) {
    return gs().testPatches[patchVersion].uniqueInteractionCount;
  }
  function getInteractedAddresses(uint256 patchVersion) external view testPatchExists(patchVersion) returns (address[] memory) {
    return gs().testPatches[patchVersion].interactedAddresses;
  }
  function getNumberOfInteractionsByAddress(uint256 patchVersion, address account) external view testPatchExists(patchVersion) returns (uint256) {
    return gs().testPatches[patchVersion].numberOfInteractions[account];
  }
  function testPatchInteraction(address account) external {
    uint256 patchVersion = gs().testPatchCount;
    if (gs().testPatches[patchVersion].numberOfInteractions[account] == 0) {
      gs().testPatches[patchVersion].uniqueInteractionCount++;
      gs().testPatches[patchVersion].interactedAddresses.push(account);
    }
    gs().testPatches[patchVersion].numberOfInteractions[account]++;
  }
  function nextPatch() external onlyAdminOrCore {
    gs().testPatchCount++;
    gs().testPatches[gs().testPatchCount].testPatchVersion++;
  }

  function pause(bool _state) external onlyAdminOrCore {
    gs().paused = _state;
  }
}

contracts/facets/MowseFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;
import "@openzeppelin/contracts/utils/Strings.sol";

import { LibDiamond } from "../libraries/LibDiamond.sol";

import { WithStorage, MOWSE_MINTER_ROLE } from "../libraries/LibStorage.sol"; 

import { MowseWearFacet } from "./MowseWearFacet.sol";
import { MowseVisualizerFacet } from "./MowseVisualizerFacet.sol";
import { AccessControlFacet } from "./AccessControlFacet.sol";
import { GameStorageFacet } from "./GameStorageFacet.sol";

contract MowseFacet is WithStorage {
  using Strings for uint256;

  event MowseMinted(address player, uint256 tokenId);

  modifier notPaused() {
    require(!gs().paused, "Contract is paused");
    _;
  }
  modifier mowseMustExist(uint256 _tokenId) {
    require(gs().mowses[_tokenId].tokenId > 0, "Mowse/must-exist");
    _;
  }
  // Only allow Mowse Minting through MowseAvatar contract
  function mintMowse(address to) external notPaused {
    require(AccessControlFacet(address(this)).hasMowseMinterRole(msg.sender), 'Mowse/Must have Minter role');
    _mint(to);
  }
  function freeMint(address to) external {
    require(AccessControlFacet(address(this)).hasMowseAdminRole(msg.sender), 'Mowse/Must have Admin role');
    _mint(to);
  }
  function mintMowsePack(address to, uint256 mowsepackId) external notPaused {
    require(AccessControlFacet(address(this)).hasMowseMinterRole(msg.sender), 'Mowse/Must have Minter role');
    _setupMowse(to, mowsepackId, 0);
    gs().mowses[mowsepackId].tokenId = mowsepackId;  // Flag for a mowse was minted
  }
  // Update MowsePack NFTs if they minted through original contract
  function updateMowsePack(address to, uint256 mowseId) external mowseMustExist(mowseId) notPaused {
    require(!gs().mowses[mowseId].mowsewearClaimed, "Mowse/mowse-already-updated");
    gs().mowses[mowseId].mowsewearClaimed = true;
    _setupMowse(to, mowseId, 0);

    GameStorageFacet(gs().diamondAddress).testPatchInteraction(msg.sender);
  }
  function mowseTokenURI(uint256 mowseId) external view mowseMustExist(mowseId) returns (string memory) {
    return MowseVisualizerFacet(gs().diamondAddress).getMowseTokenURI(mowseId);
  }
  function _mint(address to) internal {
    _setupMowse(to, gs().mowseTokenIdCounter, 2);
    gs().mowses[gs().mowseTokenIdCounter].tokenId = gs().mowseTokenIdCounter;  // Flag for a mowse was minted
    gs().mowses[gs().mowseTokenIdCounter].mowsewearClaimed = true; // Minted Mowse always true 
    gs().mowseTokenIdCounter++;
  }
  // Set up Mowse with individual MowseWear assets and other randomly generated stats
  function _setupMowse(address to, uint256 mowseId, uint16 generation) internal {
    // mint individual MowseWear assets (Base MowseTraits)
    MowseWearFacet(address(this)).mintAndWearMowseWear(to, mowseId, 0, 0, true);
    MowseWearFacet(address(this)).mintAndWearMowseWear(to, mowseId, 2, 0, true);
    MowseWearFacet(address(this)).mintAndWearMowseWear(to, mowseId, 3, 0, true);
    MowseWearFacet(address(this)).mintAndWearMowseWear(to, mowseId, 5, 0, true);
    MowseWearFacet(address(this)).mintAndWearMowseWear(to, mowseId, 6, 0, true);
    MowseWearFacet(address(this)).mintAndWearMowseWear(to, mowseId, 8, 0, true);

    // Set individual traits as base
    gs().mowses[mowseId].baseBackgroundColor = gs().mowseWearDictionary[0][0];
    gs().mowses[mowseId].baseSkinColor = gs().mowseWearDictionary[2][0];
    gs().mowses[mowseId].baseEarType = gs().mowseWearDictionary[3][0];
    gs().mowses[mowseId].baseBodyType = gs().mowseWearDictionary[5][0];
    gs().mowses[mowseId].baseMouth = gs().mowseWearDictionary[6][0];
    gs().mowses[mowseId].baseEyeType = gs().mowseWearDictionary[8][0];

    // Set generation
    gs().mowses[mowseId].generation = generation;
  }
}

contracts/facets/MowseLootboxFacet.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/utils/Strings.sol";

import { LibDiamond } from "../libraries/LibDiamond.sol";
import "../libraries/SlothVerifiableDelay.sol";

import { WithStorage } from "../libraries/LibStorage.sol"; 

import { MowseWearFacet } from "./MowseWearFacet.sol";
import { AccessControlFacet } from "./AccessControlFacet.sol";
import { GameStorageFacet } from "./GameStorageFacet.sol";

contract MowseLootboxFacet is WithStorage {
  using Strings for uint256;

  event MowseLootboxChosen(address player, uint256 tokenId);

  modifier notPaused() {
    require(!gs().paused, "Contract is paused");
    _;
  }
  function getMowseLootboxPrice(uint8 index) public view returns (uint256) {
    return gs().mowselootboxes[index].price;
  }
  function getMowseLootboxActive(uint8 index) external view returns (bool) {
    return gs().mowselootboxes[index].active;
  }
  function getMowseLootboxName(uint8 index) external view returns (string memory) {
    return gs().mowselootboxes[index].name;
  }
  function getMowseLootboxPool(uint8 index) external view returns (uint256[] memory) {
    uint256[] memory lootPool = new uint256[](gs().mowselootboxes[index].itemCount);
    for(uint16 i = 0; i < gs().mowselootboxes[index].itemCount; i++) {
      lootPool[i] = gs().mowselootboxes[index].lootPool[i];
    }
    return lootPool;
  }

  function purchaseMowseLootbox(uint8 index) external notPaused {
    uint256 lootboxPrice = getMowseLootboxPrice(index);
    require(gs().mowsegold.balanceOf(msg.sender) > lootboxPrice, 'MowseLootbox/Not enough funds');
    require(gs().mowselootboxes[index].active, 'MowseLootbox/Loot pool not active');
    require(gs().mowselootboxes[index].itemCount > 0, 'MowseLootbox/No items in loot pool');
    require(gs().mowsegold.transferFrom(msg.sender, address(this), lootboxPrice), 'MowseLootbox/Payment not successful');

    // first get vdf seed
    uint256 vdfSeed = uint256(keccak256(abi.encodePacked(msg.sender, gs().mowseLootboxNonce++, block.timestamp, blockhash(block.number - 1))));
    gs().mowseLootboxTokenIdToSeed[gs().mowseWearTokenIdCounter] = vdfSeed;
    // mint using this seed
    uint16 randomLootPoolIndex = uint16(vdfSeed % gs().mowselootboxes[index].itemCount);
    uint256 dictionaryIndex = gs().mowselootboxes[index].lootPool[randomLootPoolIndex];
    uint8 traitType = gs().mowseWearDictionaryByDictionaryIndex[dictionaryIndex].traitType;
    uint16 traitIndex = gs().mowseWearDictionaryByDictionaryIndex[dictionaryIndex].traitIndex;

    MowseWearFacet(gs().diamondAddress).mintMowseWear(msg.sender, traitType, traitIndex, false, 0);

    GameStorageFacet(gs().diamondAddress).testPatchInteraction(msg.sender);
  }
  
  // index is the loot pool index; [0-11] are trait type specific, [12] is general pool, 13+ are any other specific pools
  function updateMowseLootboxItem(uint8 index, uint256 dictionaryIndex, bool justAdding, uint16 poolIndex) external {
    require(AccessControlFacet(gs().diamondAddress).hasMowseAdminRole(msg.sender), "Only Admin addresses can perform this action");
    
    // if justAdding = true, ignore poolIndex
    if (justAdding) {
      gs().mowselootboxes[index].lootPool[gs().mowselootboxes[index].itemCount] = dictionaryIndex;
      gs().mowselootboxes[index].itemCount++;
    } else {
      require(poolIndex < gs().mowselootboxes[index].itemCount, 'MowseLootbox/Cannot update non-existent lootbox item');
      // only update traitType and traitIndex
      gs().mowselootboxes[index].lootPool[poolIndex] = dictionaryIndex;
    }
  }
  
  
  function _prove(uint256 proof, uint256 seed) internal view returns (bool) {
    return SlothVerifiableDelay.verify(proof, seed, gs().mowseLootboxPrime, gs().mowseLootboxIterations);
  }

  function getProofById(uint256 tokenId) external view returns (uint256) {
    require(gs().mowseLootboxTokenIdToSeed[tokenId] != 0, 'MowseLootbox/mowsewear not minted from lootbox');
    return SlothVerifiableDelay.compute(gs().mowseLootboxTokenIdToSeed[tokenId], gs().mowseLootboxPrime, gs().mowseLootboxIterations);
  }
  function proveById(uint256 tokenId, uint256 proof) external view returns (bool) {
    require(gs().mowseLootboxTokenIdToSeed[tokenId] != 0, 'MowseLootbox/mowsewear not minted from lootbox');
    if (_prove(proof, gs().mowseLootboxTokenIdToSeed[tokenId])) {
      return true;
    } else {
      return false;
    }
  }

  function resetMowseLootbox(uint8 index) external {
    require(AccessControlFacet(gs().diamondAddress).hasMowseAdminRole(msg.sender), "Only Admin addresses can perform this action");
    // de-active lootbox during reset
    gs().mowselootboxes[index].active = false;
    uint16 currentItemCount = gs().mowselootboxes[index].itemCount;
    for (uint16 i = 0; i < currentItemCount; i++) {
      delete gs().mowselootboxes[index].lootPool[i];
    }

    gs().mowselootboxes[index].itemCount = 0;
  }
  function setMowseLootboxActive(uint8 index, bool active) external {
    require(AccessControlFacet(gs().diamondAddress).hasMowseAdminRole(msg.sender), "Only Admin addresses can perform this action");
    gs().mowselootboxes[index].active = active;
  }
  function setMowseLootboxPrice(uint8 index, uint256 price) external {
    require(AccessControlFacet(gs().diamondAddress).hasMowseAdminRole(msg.sender), "Only Admin addresses can perform this action");
    gs().mowselootboxes[index].price = price;
  }
  function setMowseLootboxName(uint8 index, string memory name) external {
    require(AccessControlFacet(gs().diamondAddress).hasMowseAdminRole(msg.sender), "Only Admin addresses can perform this action");
    require(bytes(name).length > 0, "MowseLootbox/Lootbox must have a name");
    gs().mowselootboxes[index].name = name;
  }
}

contracts/facets/MowseVisualizerFacet.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;
import "hardhat/console.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

import { LibDiamond } from "../libraries/LibDiamond.sol";
import '../libraries/Base64.sol';
import "../libraries/LibUtils.sol";
import { WithStorage, MowseWear, MowseWearDimensions, SKILL_TYPE_NUM, EQUIPPED_WEARABLE_SLOTS } from "../libraries/LibStorage.sol"; 

import { MowseFacet } from "./MowseFacet.sol";
import { MowseWearFacet } from "./MowseWearFacet.sol";

contract MowseVisualizerFacet is WithStorage {
  using Strings for uint256;
  using Strings for uint16;
  using Strings for uint8;
  using Strings for int16;

  function getMowseTokenURI(uint256 mowseId) external view returns (string memory) {
    return
      string(
        abi.encodePacked(
          'data:application/json;base64,',
          Base64.encode(
            abi.encodePacked(
              '{',
              _getNameJSON(mowseId),
              _getGenerationJSON(mowseId, ','),
              _getSkillLevelBoostsJSON(mowseId, ','),
              _getBaseAttributesJSON(','),
              _getWearableAttributesJSON(mowseId, ','),
              _getMowseImageJSON(mowseId, ','),
              '}'
            )
          )
        )
      );
  }
  function getMowseWearTokenURI(uint256 mowseWearTokenId) external view returns (string memory) {
    return
      string(
        abi.encodePacked(
          'data:application/json;base64,',
          Base64.encode(
            abi.encodePacked(
              '{"name": "',
              gs().mowsewears[mowseWearTokenId].metadata.traitName,
              '", "description": "On chain tradeable assets to customize your Mowse.", "attributes":',
              _getMowseWearAttributeJSON(mowseWearTokenId),
              ', "image":"',
              getMowseWearImage(mowseWearTokenId),
              '"}'
            )
          )
      )
      );
  }
  function getMowseWearImageFromDictionary(uint8 traitType, uint16 traitIndex) external view returns (string memory) {
    return string(
      abi.encodePacked(
        'data:image/svg+xml;base64,',
        Base64.encode(
          abi.encodePacked(
            '<svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" width="480" height="480" viewBox="0 0 480 480"><defs><style>.cls-1{fill:#fff;opacity:0;}</style></defs><filter id="blurFilter"><feGaussianBlur stdDeviation="1"/></filter><g id="Layer_2" data-name="Layer 2"><g id="canvas" data-name="canvas"><rect class="cls-1" width="480" height="480"/></g>',
              string(_getMowseWearImageJSON(
                traitType,
                gs().mowseWearDictionary[traitType][traitIndex].traitName,
                traitIndex,
                gs().mowseWearDictionary[traitType][traitIndex].baseDimensions
              )),
            '</g></svg>'
          )
        )
      )
    );
  }
  function getMowseWearImage(uint256 mowseWearTokenId) public view returns (string memory) {
    require(MowseWearFacet(gs().diamondAddress).mowseWearExists(mowseWearTokenId), 'MowseWear does not exist');
    return string(
      abi.encodePacked(
        'data:image/svg+xml;base64,',
        Base64.encode(
          abi.encodePacked(
            '<svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" width="480" height="480" viewBox="0 0 480 480"><defs><style>.cls-1{fill:#fff;opacity:0;}</style></defs><filter id="blurFilter"><feGaussianBlur stdDeviation="1"/></filter><g id="Layer_2" data-name="Layer 2"><g id="canvas" data-name="canvas"><rect class="cls-1" width="480" height="480"/></g>',
              string(_getMowseWearImageJSON(
                gs().mowsewears[mowseWearTokenId].metadata.traitType,
                gs().mowsewears[mowseWearTokenId].metadata.traitName,
                gs().mowsewears[mowseWearTokenId].metadata.traitIndex,
                gs().mowsewears[mowseWearTokenId].dimensions
              )),
            '</g></svg>'
          )
        )
      )
    );
  }
  
  function _getNameJSON(uint256 mowseId) private pure returns (bytes memory) {
    return abi.encodePacked('"name":"Mowse #', mowseId.toString(), '"');
  }
  function _getGenerationJSON(uint256 mowseId, string memory prepend) private view returns (bytes memory) {
    return abi.encodePacked(prepend, '"generation":"', gs().mowses[mowseId].generation.toString(), '"');
  }
  function _getSkillLevelBoostsJSON(uint256 mowseId, string memory prepend) private view returns (bytes memory) {
    return abi.encodePacked(
      prepend,
      '"stats":{',
      _convertSkillStaticArrayToParts(gs().mowses[mowseId].skillLevelBoosts),
      '}');
  }
  function _getBaseAttributesJSON(string memory prepend) private view returns (bytes memory) {
    bytes memory _traitsOne = abi.encodePacked(
      _getBaseAttributeJSON(0, 0, false),
      _getBaseAttributeJSON(2, 0, true),
      _getBaseAttributeJSON(3, 0, true)
    );
    bytes memory _traitsTwo = abi.encodePacked(
      _getBaseAttributeJSON(5, 0, true),
      _getBaseAttributeJSON(6, 0, true),
      _getBaseAttributeJSON(8, 0, true)
    );
    return abi.encodePacked(prepend, '"base_attributes":[', _traitsOne, _traitsTwo, ']');
  }
  function _getBaseAttributeJSON(
    uint8 traitType,
    uint16 traitIndex,
    bool prepend
  ) internal view returns (bytes memory) {
    return
      abi.encodePacked(
        prepend ? ',' : '',
        _getMowseWearBaseAttributeJSON(traitType, traitIndex)
      );
  }
  function _getMowseWearBaseAttributeJSON(uint8 traitType, uint16 traitIndex) internal view returns (string memory) {
    return
      string(abi.encodePacked(
        '{"trait_type":"',
        WithStorage.equippedWearableSlotToString(traitType),
        '","value":"',
        gs().mowseWearDictionary[traitType][traitIndex].traitName,
        '"}'
      ));
  }
  function _getWearableAttributesJSON(uint256 mowseId, string memory prepend) private view returns (bytes memory) {
    bytes memory _traitsOne = abi.encodePacked(
      _getAttributeJSON(mowseId, 0, false),
      _getAttributeJSON(mowseId, 1, true),
      _getAttributeJSON(mowseId, 2, true)
    );
    bytes memory _traitsTwo = abi.encodePacked(
      _getAttributeJSON(mowseId, 3, true),
      _getAttributeJSON(mowseId, 4, true),
      _getAttributeJSON(mowseId, 5, true)
    );
    bytes memory _traitsThree = abi.encodePacked(
      _getAttributeJSON(mowseId, 6, true),
      _getAttributeJSON(mowseId, 7, true),
      _getAttributeJSON(mowseId, 8, true)
    );
    bytes memory _traitsFour = abi.encodePacked(
      _getAttributeJSON(mowseId, 9, true),
      _getAttributeJSON(mowseId, 10, true),
      _getAttributeJSON(mowseId, 11, true)
    );
    return abi.encodePacked(prepend, '"wearing":[', _traitsOne, _traitsTwo, _traitsThree, _traitsFour, ']');
  }
  function _getAttributeJSON(
    uint256 mowseId,
    uint8 traitType,
    bool prepend
  ) internal view returns (bytes memory) {
    uint256 mowseWearTokenId = gs().mowses[mowseId].equippedMowseWearByTokenIds[traitType];
    return
      mowseWearTokenId > 0
        ? abi.encodePacked(
          prepend ? ',' : '',
          _getMowseWearAttributeJSON(mowseWearTokenId)
        )
        : bytes('');
  }
  
  function _getMowseWearAttributeJSON(uint256 mowseWearTokenId) internal view returns (string memory) {
    uint8 traitType = gs().mowsewears[mowseWearTokenId].metadata.traitType;
    uint16 traitIndex = gs().mowsewears[mowseWearTokenId].metadata.traitIndex;

    return
      string(abi.encodePacked(
        '{"id":',
        mowseWearTokenId.toString(),
        ',"trait_type":"',
        WithStorage.equippedWearableSlotToString(traitType),
        '","value":"',
        gs().mowsewears[mowseWearTokenId].metadata.traitName,
        '",',
        _getBonusStatsJSON(
          gs().mowsewears[mowseWearTokenId].bonuses.itemRarity, 
          gs().mowsewears[mowseWearTokenId].bonuses.baseSkillLevelBoosts, 
          gs().mowsewears[mowseWearTokenId].bonuses.additionalSkillLevelBoosts
        ),
        ',"frequency":"',
        _getTraitFrequency(traitType, traitIndex),
        '"}'
      )
    );
  }
  function _getTraitFrequency(uint8 traitType, uint16 traitIndex) internal view returns (string memory) {
    uint256 _frequency = uint32((gs().mowseWearCountByTraitIndex[traitType][traitIndex] * 100000) / gs().mowseWearCountByTraitType[traitType]);
    uint256 _whole = _frequency / 1000;
    uint256 _decimals = _frequency % 1000;
    return 
      string(
        abi.encodePacked(
          _whole.toString(),
          '.',
          _decimals < 10 ? '00' : (_decimals < 100 ? '0' : ''),
          _decimals.toString(),
          '%'
        )
      );
  }
  function _getBonusStatsJSON(uint8 itemRarity, int16[SKILL_TYPE_NUM] memory baseSkillLevelBoosts, int16[SKILL_TYPE_NUM] memory additionalSkillLevelBoosts) internal pure returns (string memory) {
    string memory bonusStats = string(
        abi.encodePacked(
          '"item_rarity":"',
          WithStorage.itemRarityToString(itemRarity),
          '","base_skill_level_bonus":{',
          _convertSkillStaticArrayToParts(baseSkillLevelBoosts),
          '},"additional_skill_level_bonus":{',
          _convertSkillStaticArrayToParts(additionalSkillLevelBoosts),
          '}'
        )
      );
    return bonusStats;
  }
  function _convertSkillStaticArrayToParts(int16[SKILL_TYPE_NUM] memory skillLevels) internal pure returns (string memory) {
    return
      string(
        abi.encodePacked(
          '"charisma":',
          LibUtils.int16ToString(skillLevels[0]),
          ',"constitution":',
          LibUtils.int16ToString(skillLevels[1]),
          ',"dexterity":',
          LibUtils.int16ToString(skillLevels[2]),
          ',"intelligence":',
          LibUtils.int16ToString(skillLevels[3]),
          ',"luck":',
          LibUtils.int16ToString(skillLevels[4]),
          ',"strength":',
          LibUtils.int16ToString(skillLevels[5]),
          ',"wisdom":',
          LibUtils.int16ToString(skillLevels[6]),
          ''
        )
      );
  }
  function _getMowseImageJSON(uint256 mowseId, string memory prepend) private view returns (bytes memory) {
    return abi.encodePacked(prepend, '"image":"data:image/svg+xml;base64,', Base64.encode(_getSVG(mowseId)), '"');
  }
  function _getSVG(uint256 mowseId) private view returns (bytes memory) {
    bytes memory _imagesOne = abi.encodePacked(
      _getSvgLayer(mowseId, 0, true),
      _getSvgLayer(mowseId, 1, false),
      _getSvgLayer(mowseId, 2, true),
      _getSvgLayer(mowseId, 3, true),
      _getSvgLayer(mowseId, 4, false),
      _getSvgLayer(mowseId, 5, true)
    );
    bytes memory _imagesTwo = abi.encodePacked(
      _getSvgLayer(mowseId, 6, true),
      _getSvgLayer(mowseId, 7, false),
      _getSvgLayer(mowseId, 8, true),
      _getSvgLayer(mowseId, 9, false),
      _getSvgLayer(mowseId, 10, false),
      _getSvgLayer(mowseId, 11, false)
    );
    return
      abi.encodePacked(
        '<svg xmlns="http://www.w3.org/2000/svg" xmlns:xlink="http://www.w3.org/1999/xlink" width="480" height="480" viewBox="0 0 480 480"><defs><style><![CDATA[]]></style><filter id="blur" y="-5" height="40"><feGaussianBlur in="SourceGraphic" stdDeviation="5" y="-"/></filter></defs><g id="Mowse" data-name="Mowse">',
        _imagesOne,
        _imagesTwo,
        '</g></svg>'
      );
    }

    function _getSvgLayer(uint256 mowseId, uint8 traitType, bool defaultIfEmpty) internal view returns (bytes memory) {
    uint256 mowseWearTokenId = gs().mowses[mowseId].equippedMowseWearByTokenIds[traitType];
    return
      mowseWearTokenId > 0
        ? bytes(_getMowseWearImageJSON(
            traitType,
            gs().mowsewears[mowseWearTokenId].metadata.traitName,
            gs().mowsewears[mowseWearTokenId].metadata.traitIndex,
            gs().mowsewears[mowseWearTokenId].dimensions
          ))
        : (
          defaultIfEmpty 
          ? bytes(_getMowseWearImageJSON(
            traitType,
            gs().mowseWearDictionary[traitType][0].traitName,
            0,
            gs().mowseWearDictionary[traitType][0].baseDimensions
            )) 
          : bytes(''));
    }
    function _getMowseWearImageJSON(
      uint8 traitType,
      string memory traitName,
      uint16 traitIndex,
      MowseWearDimensions memory dimensions
    ) internal pure returns(bytes memory imageJSON) {
        return bytes (
              abi.encodePacked(
                '<g id="',
                string(abi.encodePacked(traitType.toString(), '_', traitIndex.toString())),
                '" data-name="',
                traitName,
                '">',
                (
                  keccak256(abi.encodePacked(traitName)) == keccak256(abi.encodePacked("None")) ? 
                    abi.encodePacked('<g></g>')
                  :
                  abi.encodePacked(
                      '<image id="',
                      string(abi.encodePacked(traitType.toString(), '_', traitIndex.toString())),
                      '_Image" data-name="',
                      traitName,
                      ' Image" width="',
                      dimensions.width.toString(),
                      '" height="',
                      dimensions.height.toString(),
                      '" transform="',
                      dimensions.transform,
                      '" style="',
                      dimensions.style,
                      '" xlink:href="',
                      dimensions.image,
                      '"/>'
                  )
                ),
                '</g>'
            )
        );
    }
}

